
"use strict"





require('babel-polyfill')
var is = require('is')





var constants    = require('gibbr/commons/constants')
var renameInodes = require('gibbr/fs/rename-inodes')





var gibbr = rawArgs => {

	var args = gibbr.preproccess(gibbr.validate(rawArgs))

	args['--version']
		? console.log(constants.pkg.version)
		: renameInodes(args)

}





gibbr.validate = args => {
	return args
}

gibbr.preproccess = rawArgs => {

	try {

		var size = parseInt(rawArgs['--size'], 10)

	} catch (err) {
		throw new Error('failed to parse "--size" argument.')
	}

	if (size !== size) {
		throw Error('"--size" was NaN.')
	}

	if (size < 0) {
		throw Error('"--size" must be larger than zero.')
	}

	is.always.boolean(rawArgs['--no-preserve-root'])

	return {
		['-']:      rawArgs['-'],
		files:      rawArgs['<path>'],
		size:       size,
		shouldNuke: rawArgs['--no-preserve-root']

	}

}





module.exports = gibbr
