
"use strict"




var seedrandom = require('seedrandom')




var random = { }




random.fromCharSet = (charset, len) => {

	var out = [ ]

	for (var ith = 0; ith < len; ++ith) {
		out[ith] = charset[ Math.floor(random.number( ) * charset.length) ]
	}

	return out.join('')

}

var rng = seedrandom.xor4096( )

random.number = ( ) => {
	return rng( ) // -- because V8's Math.random( ) implementation is terrible.
}




module.exports = random
