
"use strict"





var path      = require('path')
var commons   = require('gibbr/commons/commons')
var constants = require('gibbr/commons/constants')
var random    = require('gibbr/commons/random')





var randomName = { }

randomName.directory = (fpath, size) => {

	var basename = random.fromCharSet(constants.charset.LETTERS, size)
	return path.join(path.dirname(fpath), basename)

}

randomName.file = (fpath, size) => {

	var basename = random.fromCharSet(constants.charset.LETTERS, size) + commons.pathExtension(fpath)
	return path.join(path.dirname(fpath), basename)

}





module.exports = randomName
