
"use strict"




var is         = require('is')

var commons    = require('gibbr/commons/commons')
var renamePath = require('gibbr/fs/rename-path')





var renameInodes = args => {

	renameInodes.precond(args)

	args['-']
		? commons.mapStdin(fpath => {
			renamePath(fpath, args.shouldNuke, args.size)
		})
		: args.files.forEach(fpath => {
			renamePath(fpath, args.shouldNuke, args.size)
		})

}

renameInodes.precond = args => {

	is.always.array(args.files)
	is.always.number(args.size)

}





module.exports = renameInodes
